tabs.content:
/**
 * 增材制造狗公众号样式配置
 * 主题色：荧光黄绿 rgba(220, 249, 80, 1)
 * 适用于3D打印和增材制造内容
 */

/* 顶层容器样式 */
container {
  font-family: -apple-system-font, BlinkMacSystemFont, Helvetica Neue, PingFang SC, Hiragino Sans GB, Microsoft YaHei UI, Microsoft YaHei, Arial, sans-serif;
}

/* 一级标题样式 */
h1 {
  color: #333;
  font-size: 22px;
  font-weight: bold;
  line-height: 1.4;
  margin: 20px 0 15px 0;
  text-align: left;
}

/* 二级标题样式 */
h2 {
  color: #333;
  font-size: 18px;
  font-weight: bold;
  margin: 25px 0 12px 0;
  border-bottom: 2px solid rgba(220, 249, 80, 1);
  padding-bottom: 5px;
}

/* 三级标题样式 */
h3 {
  color: #333;
  font-size: 16px;
  font-weight: bold;
  margin: 20px 0 10px 0;
}

/* 四级标题样式 */
h4 {
  color: #555;
  font-size: 15px;
  font-weight: bold;
  margin: 18px 0 8px 0;
}

/* 段落样式 */
p {
  color: #333;
  line-height: 1.8;
  margin-bottom: 16px;
  text-align: justify;
  font-size: 15px;
  text-indent: 2em;
}

/* 信源段落特殊样式 */
p:first-child {
  font-size: 12px;
  color: #999;
  text-indent: 0;
  margin-bottom: 20px;
  text-align: left;
}

/* 图片样式 */
image {
  border-radius: 6px;
  margin: 20px 0;
  max-width: 100%;
  height: auto;
}

/* 引用样式 */
blockquote {
  background-color: #f8f9fa;
  border-left: 4px solid rgba(220, 249, 80, 1);
  padding: 15px 20px;
  margin: 20px 0;
  color: #555;
  font-style: italic;
}

/* 引用段落样式 */
blockquote_p {
  color: #555;
  margin: 0;
  text-indent: 0;
}

/* 粗体样式 */
strong {
  color: #333;
  font-weight: bold;
}

/* 链接样式 */
link {
  color: #007acc;
  text-decoration: none;
}

/* 微信链接样式 */
wx_link {
  color: #007acc;
  text-decoration: none;
}

/* 行内代码样式 */
codespan {
  background-color: rgba(220, 249, 80, 0.2);
  padding: 2px 6px;
  border-radius: 3px;
  font-family: 'Courier New', Courier, monospace;
  color: #333;
  font-size: 13px;
}

/* 代码块样式 */
code {
  background-color: #f6f8fa;
  border: 1px solid #e1e4e8;
  border-radius: 6px;
  padding: 16px;
  font-family: 'Courier New', Courier, monospace;
  font-size: 13px;
  line-height: 1.4;
}

/* 代码块外层样式 */
code_pre {
  margin: 20px 0;
}

/* 分割线样式 */
hr {
  border: none;
  border-top: 1px solid #eee;
  margin: 30px 0;
}

/* 有序列表样式 */
ol {
  margin: 16px 0;
  padding-left: 25px;
}

/* 无序列表样式 */
ul {
  margin: 16px 0;
  padding-left: 25px;
}

/* 列表项样式 */
listitem {
  margin-bottom: 8px;
  line-height: 1.6;
  text-indent: 0;
}

/* 斜体样式 */
em {
  font-style: italic;
  color: #555;
}

/* 品牌标识样式 - 用于绿色框 */
.brand-header {
  background: linear-gradient(135deg, rgba(220, 249, 80, 0.9), rgba(180, 220, 60, 0.9));
  color: #333;
  font-weight: bold;
  padding: 12px 20px;
  border-radius: 8px;
  text-align: center;
  margin: 20px 0;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}
