tabs.content:
/* 微信公众号样式配置 */
/* 一级标题样式 */
h1 {
  color: #333;
  font-size: 24px;
  font-weight: bold;
  text-align: center;
  margin: 20px 0;
  line-height: 1.4;
}

/* 二级标题样式 */
h2 {
  color: #333;
  font-size: 20px;
  font-weight: bold;
  margin: 25px 0 15px 0;
  padding-bottom: 8px;
  border-bottom: 2px solid #07c160;
}

/* 三级标题样式 */
h3 {
  color: #333;
  font-size: 18px;
  font-weight: bold;
  margin: 20px 0 10px 0;
}

/* 段落样式 */
p {
  color: #333;
  line-height: 1.8;
  margin-bottom: 16px;
  text-align: justify;
  font-size: 16px;
}

/* 引用样式 */
blockquote {
  background-color: #f7f7f7;
  border-left: 4px solid #07c160;
  padding: 15px 20px;
  margin: 20px 0;
  font-style: italic;
  color: #666;
}

/* 引用段落样式 */
blockquote_p {
  color: #666;
  margin: 0;
}

/* 粗体样式 */
strong {
  color: #333;
  font-weight: bold;
}

/* 链接样式 */
link {
  color: #576b95;
  text-decoration: none;
}

/* 微信链接样式 */
wx_link {
  color: #576b95;
  text-decoration: none;
}

/* 图片样式 */
image {
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
  margin: 20px 0;
}

/* 代码块样式 */
code {
  background-color: #f8f8f8;
  border: 1px solid #e8e8e8;
  border-radius: 6px;
  padding: 16px;
  font-family: 'Courier New', Courier, monospace;
}

/* 行内代码样式 */
codespan {
  background-color: #f3f4f6;
  padding: 2px 6px;
  border-radius: 3px;
  font-family: 'Courier New', Courier, monospace;
  color: #e83e8c;
}

/* 分割线样式 */
hr {
  border: none;
  border-top: 1px solid #eee;
  margin: 30px 0;
}

/* 无序列表样式 */
ul {
  margin: 16px 0;
  padding-left: 20px;
}

/* 有序列表样式 */
ol {
  margin: 16px 0;
  padding-left: 20px;
}

/* 列表项样式 */
listitem {
  margin-bottom: 8px;
  line-height: 1.6;
}
